import './dropdown.css';
export const Dropdown = () => {
	let toggle = 'show';
	const click = () => {
		if (toggle === 'show') {
			toggle = 'hide';
		} else {
			toggle = 'show';
		}
	};
	return (
		<>
			<h4>Should you use a dropdown?</h4>
			<div className='hover'>Select</div>
			<div className={hover ? 'show' : 'hide'}>
				<p
					className={toggle}
					onClick={click}>
					Yes
				</p>
				<p
					className={toggle}
					onClick={click}>
					Probably not
				</p>
			</div>
		</>
	);
};
